mapper.select:
    SELECT a.id userInfoId, b.username,c.id bookId,c.bookName,a.subTime  FROM userbook a ,userinfo b ,book c
			   WHERE a.userInfoId=b.id
			     AND a.bookId=c.id
			      AND b.username=#{username}
			     AND c.bookName==#{bookName}
			     AND b.vipLevel=#{vipLevel}
			     
			           and a.subTime >= #{startTime}
			     
			     
			          and a.subTime <= #{endTime}

mapper.update:
update userinfo t set 
        t.status=0
  where t.id=#{id}
