mapper.select:
	SELECT * FROM userinfo t WHERE 1=1
		 and username=#{username}
        and vipLevel=#{vipLevel}

mapper.update:
update userinfo t set 
        t.status=0
  where t.id=#{id}

mapper.select:
SELECT a.id ,a.userInfoId ,b.levelName,a.startTime ,a.endTime,a.price FROM userinfo_level a , vip_level b
  WHERE a.vipLevelId =b.id
   AND a.userInfoId=#{id}

	SELECT a.bookId,b.bookName,a.subTime,a.lastTime,a.userId FROM userbook a, book b
   WHERE a.bookId=b.id
     AND a.userId=#{id}
