mapper.select:
    SELECT t.id ,t.price ,t.priceStatus ,t.subjectId ,a.subjectName,t.vipLevelId FROM viplevel_price t ,viplevel_subject a
	   WHERE t.subjectId=a.id
	    AND t.id=#{subjectPriceId}

mapper.update:
		  update viplevel_price t set 
                 t.price=#{price},
                 t.priceStatus=#{priceStatus},
                 t.updateTime=#{updateTime},
                 t.updateId=#{updateId}
          where t.id=#{id}
